function Banner(){this.bannerGroup=$("#banner-group"),this.index=0,this.leftArrow=$(".left-arrow"),this.rightArrow=$(".right-arrow"),this.listenBannerHover()}Banner.prototype.toggleArrow=function(r){var n=this;r?(n.leftArrow.show(),n.rightArrow.show()):(n.leftArrow.hide(),n.rightArrow.hide())},Banner.prototype.listenBannerHover=function(){var r=this;this.bannerGroup.hover(function(){clearInterval(r.timer),r.toggleArrow(!0)},function(){r.loop(),r.toggleArrow(!1)})},Banner.prototype.loop=function(){var r=this,n=document.getElementById("banner-url");this.timer=setInterval(function(){1<=r.index?(r.index=0,n.style.left=r.index+"px"):(r.index++,n.style.left=n.offsetLeft+-798*r.index+"px")},1e3)},Banner.prototype.run=function(){this.loop()},$(function(){(new Banner).run()});